type Todo {
  id: Int!
  text: String!
  completed: Boolean!
}

type Mutation {
  addTodo(text: String!): Todo
  toggleTodo(id: Int!): Todo
}

type Query {
  visibilityFilter: String
  todos: [Todo]
}

query GetDogByBreed($breed: String!) {
  dog(breed: $breed) {
    images {
      url
      id
      isLiked @client
    }
  }
}
