query MoreComments($cursor: String) {
  moreComments(cursor: $cursor) {
    cursor
    comments {
      author
      text
    }
  }
}

query Comments($cursor: String) {
  Comments(first: 10, after: $cursor) {
    edges {
      node {
        author
        text
      }
    }
    pageInfo {
      endCursor
      hasNextPage
    }
  }
}

