mutation UpdateDisplayImage($id: String!, $displayImage: String!) {
  updateDisplayImage(id: $id, displayImage: $displayImage) {
    id
    displayImage
  }
}

query GetDogByBreed($breed: String!) {
  dog(breed: $breed) {
    images {
      url
      id
      isLiked @client
    }
  }
}
